package vn.techres.order.online.common.model;import lombok.Data;import lombok.Getter;import lombok.Setter;import org.json.JSONException;import org.json.JSONObject;import vn.techres.order.online.common.utils.Constants;import java.math.BigDecimal;@Setter@Getter@Datapublic class OrderRealtimeModel extends RealtimeBaseModel{    private int orderId;    private int restaurantId;    private int restaurantBrandId;    private int branchId;    private String tableName = "ORDER ONLINE";    private String customerName;    private String customerPhone;    private String customerAddress;    private BigDecimal totalAmount;    private int paymentMethod;    private int paymentStatus;    private int customerOrderStatus;    public OrderRealtimeModel() {    }    public String getOnlyOrderOnlinePath() {        return String.format("%s/%s", this.getBranchPath(), Constants.SOCKET_IO_ORDER_ONLINE_PATH);    }    public JSONObject toJSON() throws JSONException {        JSONObject jo = super.toJSON();        jo.put("restaurant_id", this.getTableName());        jo.put("restaurant_brand_id", this.getRestaurantBrandId());        jo.put("branch_id", this.getBranchId());        jo.put("order_id", this.getOrderId());        jo.put("table_name", this.getTableName());        jo.put("total_amount", this.getTotalAmount());        jo.put("payment_method", this.getPaymentMethod());        jo.put("payment_status", this.getPaymentMethod());        jo.put("customer_name", this.getCustomerName());        jo.put("customer_phone", this.getCustomerPhone());        jo.put("customer_address", this.getCustomerAddress());        jo.put("customer_order_status", this.getCustomerOrderStatus());        jo.put("only_order_online_only_path", this.getOnlyOrderOnlinePath());        return jo;    }}