package vn.techres.order.online.common.model;import lombok.Data;import lombok.Getter;import lombok.Setter;import org.json.JSONException;import vn.techres.order.online.common.utils.Constants;import org.json.JSONObject;@Setter@Getter@Datapublic class RealtimeBaseModel {    private int restaurantId;    private int branchId;    public String getRestaurantPath() {        return String.format("%s/%d", Constants.SOCKET_IO_RESTAURANT_PATH, this.getRestaurantId());    }    public String getBranchPath() {        return String.format("%s/%s/%d", this.getRestaurantPath(), Constants.SOCKET_IO_BRANCH_PATH, this.getBranchId());    }    public JSONObject toJSON() throws JSONException {        JSONObject jo = new JSONObject();        jo.put("restaurant_id", this.getRestaurantId());        jo.put("branch_id", this.getBranchId());        jo.put("restaurant_path", this.getRestaurantPath());        jo.put("branch_path", this.getBranchPath());        return jo;    }}