package vn.techres.order.online.configuration.validation;import jakarta.validation.Constraint;import jakarta.validation.Payload;import java.lang.annotation.Documented;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;import java.lang.annotation.Target;import static java.lang.annotation.ElementType.FIELD;import static java.lang.annotation.ElementType.METHOD;@Documented@Constraint(validatedBy = PhoneValidator.class)@Retention(RetentionPolicy.RUNTIME)@Target({ FIELD, METHOD })public @interface PhoneValidatorConstraint {    String message() default "Số điện thoại không đúng định dạng";    Class<?>[] groups() default {};    Class<? extends Payload>[] payload() default {};}