package vn.techres.order.online.v1.response;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;@AllArgsConstructor@NoArgsConstructor@Datapublic class RestaurantBrandResponse {    private int id;    private String name;    private String banner;    @JsonProperty("logo_url")    private String logoUrl;    @JsonProperty("branches")    private List<BranchResponse> branches;    public RestaurantBrandResponse(vn.techres.microservice.grpc.java.elasticsearch.order_online_qr.RestaurantBrandResponse entity)  {        this.id = entity.getId();        this.name = entity.getName();        this.banner = entity.getBanner();        this.logoUrl = entity.getLogoUrl();        this.branches = new BranchResponse().mapToList(entity.getBranchesList());    }    public List<RestaurantBrandResponse> mapToList(List<vn.techres.microservice.grpc.java.elasticsearch.order_online_qr.RestaurantBrandResponse> entities) {        return entities.stream().map(RestaurantBrandResponse::new).toList();    }}