package vn.techres.order.online.v1.response;import com.fasterxml.jackson.annotation.JsonProperty;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import java.util.List;@Data@AllArgsConstructor@NoArgsConstructorpublic class RestaurantResponse {    private int id;    private String name;    private String banner;    private String logo;    @JsonProperty("restaurant_brands")    private List<RestaurantBrandResponse> restaurantBrands;    public RestaurantResponse(vn.techres.microservice.grpc.java.elasticsearch.order_online_qr.RestaurantResponse entity) {        this.id = entity.getId();        this.name = entity.getName();        this.banner = entity.getBanner();        this.logo = entity.getLogo();        this.restaurantBrands = new RestaurantBrandResponse().mapToList(entity.getRestaurantBrandsList());    }}