package vn.techres.order.online.v1.entityJson;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@NoArgsConstructor@AllArgsConstructorpublic class CustomerOrderQRCodeJson {    @Schema(description = "Id nhà hàng")    @JsonProperty("restaurant_id")    private int restaurantId;    @Schema(description = "Tên nhà hàng")    @JsonProperty("restaurant_name")    private String restaurantName;    @Schema(description = "Id thương")    @JsonProperty("restaurant_brand_id")    private int restaurantBrandId;    @Schema(description = "Id chi nhánh")    @JsonProperty("branch_id")    private int branchId;    @Schema(description = "Id khu vực")    @JsonProperty("area_id")    private int areaId;    @Schema(description = "Id bàn")    @JsonProperty("table_id")    private int tableId;    @Schema(description = "Tên bàn")    @JsonProperty("table_name")    private String tableName;    public CustomerOrderQRCodeJson(int restaurantId, int restaurantBrandId, int branchId, int areaId, int tableId) {        this.restaurantId = restaurantId;        this.restaurantBrandId = restaurantBrandId;        this.branchId = branchId;        this.areaId = areaId;        this.tableId = tableId;    }}