package vn.techres.order.online.v1.request;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import jakarta.validation.constraints.Min;import jakarta.validation.constraints.NotNull;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import org.hibernate.validator.constraints.Length;import vn.techres.order.online.configuration.validation.PhoneValidatorConstraint;@Data@AllArgsConstructor@NoArgsConstructorpublic class ChangeInfoCustomerRequest {    @Schema(description = "Id nhà hàng không để trống")    @Min(value = 1, message = "restaurant_id truyền vào phải lớn hơn 0")    @JsonProperty("restaurant_id")    private int restaurantId;    @Schema(description = "Id thương hiệu không để trống")    @Min(value = 1, message = "restaurant_brand_id truyền vào phải lớn hơn 0")    @JsonProperty("restaurant_brand_id")    private int restaurantBrandId;    @Schema(description = "Id chi nhánh không để trống")    @Min(value = 1, message = "branch_id truyền vào phải lớn hơn 0")    @JsonProperty("branch_id")    private int branchId;    @Schema(description = "Tên khách đặt không được phép truyền NULL hoặc rỗng")    @NotNull(message = "Tên khách không được phép truyền NULL")    @Length(min = 1, max = 255, message = "Tên từ 3 -> 255 ký tự")    @JsonProperty("customer_name")    private String customerName;    @Schema(description = "Số điện thoại không được phép truyền NULL hoặc rỗng")    @NotNull(message = "Số điện thoại không được phép truyền NULL")    @PhoneValidatorConstraint(message = "Số điện thoại không hợp lệ")    private String phone;    @Schema(description = "Địa chỉ nhận không được phép truyền NULL hoặc rỗng")    @NotNull(message = "Địa chỉ không được phép truyền NULL")    @Length(max = 555, message = "Ghi chú tối đa 555 ký tự")    private String address;}