package vn.techres.order.online.v1.request;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import jakarta.validation.constraints.Max;import jakarta.validation.constraints.Min;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructorpublic class ChangeStatusRequest {    @Schema(description = "Id nhà hàng không để trống")    @Min(value = 1, message = "restaurant_id truyền vào phải lớn hơn 0")    @JsonProperty("restaurant_id")    private int restaurantId;    @Schema(description = "Id thương hiệu không để trống")    @Min(value = 1, message = "restaurant_brand_id truyền vào phải lớn hơn 0")    @JsonProperty("restaurant_brand_id")    private int restaurantBrandId;    @Schema(description = "Id chi nhánh không để trống")    @Min(value = 1, message = "branch_id truyền vào phải lớn hơn 0")    @JsonProperty("branch_id")    private int branchId;    @Schema(description = "Trạng thái bill")    @Min(value = 0, message = "customer_order_status truyền vào phải [ 0 : PENDING, 1 : COMPLETE, 2 : CANCEL] <= 0")    @Max(value = 2, message = "customer_order_status truyền vào phải [ 0 : PENDING, 1 : COMPLETE, 3 : CANCEL] >= 2")    @JsonProperty("customer_order_status")    private int customerOrderStatus = -1;}