package vn.techres.order.online.v1.request;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import jakarta.validation.constraints.Min;import lombok.Data;@Datapublic class CustomerOrderCancelRequest {    @Schema(description = "Id nhà hàng không để trống")    @Min(value = 1, message = "restaurant_id truyền vào phải lớn hơn 0")    @JsonProperty("restaurant_id")    private int restaurantId;    @Schema(description = "Id thương hiệu không để trống")    @Min(value = 1, message = "restaurant_brand_id truyền vào phải lớn hơn 0")    @JsonProperty("restaurant_brand_id")    private int restaurantBrandId;    @Schema(description = "Id chi nhánh không để trống")    @Min(value = 1, message = "branch_id truyền vào phải lớn hơn 0")    @JsonProperty("branch_id")    private int branchId;    @Schema(description = "Id khu vực không để trống")    @Min(value = 1, message = "area_id truyền vào phải lớn hơn 0")    @JsonProperty("area_id")    private int areaId;    @Schema(description = "Id bàn không để trống")    @Min(value = 1, message = "table_id truyền vào phải lớn hơn 0")    @JsonProperty("table_id")    private int tableId;    @Schema(description = "Id bill không để trống")    @Min(value = 1, message = "customer_order_id truyền vào phải lớn hơn 0")    @JsonProperty("customer_order_id")    private int customerOrderId;}