package vn.techres.order.online.v1.request;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import jakarta.validation.constraints.DecimalMax;import jakarta.validation.constraints.DecimalMin;import jakarta.validation.constraints.NotEmpty;import jakarta.validation.constraints.NotNull;import lombok.Data;import org.hibernate.validator.constraints.Length;import java.math.BigDecimal;@Datapublic class OnlineOrderQRCodeRequest {    @Schema(description = "Id thiết bị không được để trống")    @NotEmpty(message = "device_id truyền không được phép truyền NULL hoặc rỗng")    @Length(max = 255, message = "device_id tối đa 255 ký tự")    @JsonProperty("device_id")    private String deviceId;    @Schema(description = "QR code không được phép truyền NULL hoặc rỗng")    @NotEmpty(message = "qr_code truyền không được phép truyền NULL hoặc rỗng")    @Length(max = 255, message = "qr_code tối đa 255 ký tự")    @JsonProperty("qr_code")    private String qrCode;    @NotNull(message = "lat không được phép là NULL")    @DecimalMin(value = "-90.0", message = "lat phải lớn hơn hoặc bằng -90.0")    @DecimalMax(value = "90.0", message = "lat phải nhỏ hơn hoặc bằng 90.0")    private BigDecimal lat;    @NotNull(message = "lng không được phép là NULL")    @DecimalMin(value = "-180.0", message = "lng phải lớn hơn hoặc bằng -180.0")    @DecimalMax(value = "180.0", message = "lng phải nhỏ hơn hoặc bằng 180.0")    private BigDecimal lng;}