package vn.techres.order.online.v1.service.impl;import jakarta.transaction.Transactional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import vn.techres.order.online.v1.dao.CustomerOrderDetailDao;import vn.techres.order.online.v1.entity.CustomerOrderDetail;import vn.techres.order.online.v1.service.CustomerOrderDetailService;import vn.techres.order.online.v1.version.VersionService;import java.util.List;@Service("CustomerOrderDetailService" + VersionService.VERSION)@Transactional(rollbackOn = Exception.class)public class CustomerOrderOnlineDetailServiceImpl implements CustomerOrderDetailService {    @Autowired    private CustomerOrderDetailDao dao;    @Override    public List<CustomerOrderDetail> findOnlineOrderDetailByOnlineOrderId(int onlineOrderId) throws Exception {        return dao.findOnlineOrderDetailByOnlineOrderId(onlineOrderId);    }    @Override    public CustomerOrderDetail findOne(long id) {        return dao.findOne(id);    }    @Override    public void delete(CustomerOrderDetail customerOrderDetail) {            dao.delete(customerOrderDetail);    }    @Override    public void deleteCustomerOrderDetail(String customerOrderIds) throws Exception {        dao.deleteCustomerOrderDetail(customerOrderIds);    }    @Override    public List<CustomerOrderDetail> findByCustomerOrderOnlineId(int customerOrderId) {        return dao.findByCustomerOrderOnlineId(customerOrderId);    }}